import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000';

export default function KnowledgeBase() {
  const [tests, setTests] = useState([]);
  const [meds, setMeds] = useState([]);
  const [selectedTest, setSelectedTest] = useState(null);
  const [selectedMed, setSelectedMed] = useState(null);
  const [testDetail, setTestDetail] = useState(null);
  const [medDetail, setMedDetail] = useState(null);
  const [activeTab, setActiveTab] = useState('tests');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchLists = async () => {
      try {
        const [testsRes, medsRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/knowledge/tests`),
          axios.get(`${API_BASE_URL}/knowledge/medications`),
        ]);
        setTests(testsRes.data.tests || []);
        setMeds(medsRes.data.medications || []);
      } catch (err) {
        // Use fallback data if API not running
        setTests(['Hemoglobin', 'WBC', 'RBC', 'FBS', 'HbA1c', 'SGPT', 'SGOT', 'Cholesterol', 'LDL', 'HDL', 'Triglycerides', 'Creatinine', 'TSH']);
        setMeds(['Metformin', 'Aspirin', 'Atorvastatin', 'Lisinopril', 'Amlodipine', 'Omeprazole', 'Paracetamol', 'Insulin', 'Amoxicillin', 'Azithromycin']);
      }
    };
    fetchLists();
  }, []);

  const loadTestDetail = async (name) => {
    setSelectedTest(name);
    setTestDetail(null);
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/knowledge/test/${name}`);
      setTestDetail(res.data);
    } catch { setTestDetail(null); }
    setLoading(false);
  };

  const loadMedDetail = async (name) => {
    setSelectedMed(name);
    setMedDetail(null);
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/knowledge/medication/${name}`);
      setMedDetail(res.data);
    } catch { setMedDetail(null); }
    setLoading(false);
  };

  return (
    <div>
      <div style={s.header}>
        <h1 style={s.title}>üìö Medical Knowledge Base</h1>
        <p style={s.sub}>Learn about common lab tests and medications in simple language</p>
      </div>

      {/* Tabs */}
      <div style={s.tabs}>
        <button style={{ ...s.tab, ...(activeTab === 'tests' ? s.tabActive : {}) }}
          onClick={() => setActiveTab('tests')}>üß™ Lab Tests</button>
        <button style={{ ...s.tab, ...(activeTab === 'meds' ? s.tabActive : {}) }}
          onClick={() => setActiveTab('meds')}>üíä Medications</button>
      </div>

      {/* Content */}
      <div style={s.layout}>
        {/* List */}
        <div style={s.listCard}>
          {activeTab === 'tests' ? (
            tests.map(name => (
              <div key={name} style={{ ...s.listItem, ...(selectedTest === name ? s.listItemActive : {}) }}
                onClick={() => loadTestDetail(name)}>
                üß¨ {name}
              </div>
            ))
          ) : (
            meds.map(name => (
              <div key={name} style={{ ...s.listItem, ...(selectedMed === name ? s.listItemActive : {}) }}
                onClick={() => loadMedDetail(name)}>
                üíä {name}
              </div>
            ))
          )}
        </div>

        {/* Detail */}
        <div style={s.detailCard}>
          {loading && <div style={s.loading}>Loading...</div>}

          {activeTab === 'tests' && testDetail && (
            <div>
              <h2 style={s.detailTitle}>{testDetail.name}</h2>
              <p style={s.detailDesc}>{testDetail.description}</p>
              <div style={s.rangeGrid}>
                <div style={s.rangeBox}>
                  <div style={s.rangeLabel}>Male Normal Range</div>
                  <div style={s.rangeValue}>{testDetail.normal_range_male}</div>
                </div>
                <div style={s.rangeBox}>
                  <div style={s.rangeLabel}>Female Normal Range</div>
                  <div style={s.rangeValue}>{testDetail.normal_range_female}</div>
                </div>
              </div>
              <div style={{ ...s.meaningBox, background: '#FEF3C7' }}>
                <strong>üî∫ If HIGH:</strong> {testDetail.high_meaning}
              </div>
              <div style={{ ...s.meaningBox, background: '#DBEAFE' }}>
                <strong>üîª If LOW:</strong> {testDetail.low_meaning}
              </div>
            </div>
          )}

          {activeTab === 'meds' && medDetail && (
            <div>
              <h2 style={s.detailTitle}>{medDetail.name}</h2>
              <span style={s.categoryBadge}>{medDetail.category}</span>
              <div style={s.infoBox}>
                <strong>üéØ Purpose:</strong> {medDetail.purpose}
              </div>
              <div style={s.meaningBox}>
                <strong>‚ö†Ô∏è Side Effects:</strong>
                <ul style={{ marginTop: '8px', paddingLeft: '20px' }}>
                  {medDetail.side_effects.map((se, i) => <li key={i}>{se}</li>)}
                </ul>
              </div>
              <div style={{ ...s.meaningBox, background: '#F0FDF4' }}>
                <strong>üìã Precautions:</strong> {medDetail.precautions}
              </div>
            </div>
          )}

          {!loading && !testDetail && !medDetail && (
            <div style={s.placeholder}>
              <div style={s.placeholderIcon}>{activeTab === 'tests' ? 'üß™' : 'üíä'}</div>
              <p style={s.placeholderText}>
                Select a {activeTab === 'tests' ? 'lab test' : 'medication'} from the list to learn more
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

const s = {
  header: { padding: '1.5rem 0 1rem', textAlign: 'center' },
  title: { fontSize: '28px', fontWeight: '800', color: '#1E1B4B', marginBottom: '6px' },
  sub: { color: '#6B7280', fontSize: '15px' },
  tabs: { display: 'flex', gap: '8px', marginBottom: '20px' },
  tab: {
    padding: '10px 24px',
    border: '2px solid #E5E7EB',
    borderRadius: '10px',
    background: 'white',
    cursor: 'pointer',
    fontWeight: '600',
    fontSize: '14px',
    color: '#6B7280',
  },
  tabActive: { borderColor: '#4F46E5', color: '#4F46E5', background: '#EEF2FF' },
  layout: { display: 'grid', gridTemplateColumns: '260px 1fr', gap: '20px' },
  listCard: {
    background: 'white',
    borderRadius: '16px',
    padding: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
    maxHeight: '600px',
    overflowY: 'auto',
  },
  listItem: {
    padding: '12px 16px',
    borderRadius: '10px',
    cursor: 'pointer',
    fontSize: '14px',
    color: '#374151',
    fontWeight: '500',
    transition: 'all 0.15s',
    marginBottom: '2px',
  },
  listItemActive: { background: '#EEF2FF', color: '#4F46E5', fontWeight: '700' },
  detailCard: {
    background: 'white',
    borderRadius: '16px',
    padding: '28px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
    minHeight: '400px',
  },
