import React, { useState, useCallback } from 'react';
import axios from 'axios';

const API = 'http://localhost:8000';
const RC = {
  HIGH:   { color:'#fb7185', bg:'rgba(251,113,133,0.08)', border:'rgba(251,113,133,0.25)', icon:'üî¥', msg:'Please consult your doctor as soon as possible.' },
  MEDIUM: { color:'#f59e0b', bg:'rgba(245,158,11,0.08)',  border:'rgba(245,158,11,0.25)',  icon:'üü°', msg:'Schedule a doctor appointment this week.' },
  LOW:    { color:'#34d399', bg:'rgba(52,211,153,0.08)',  border:'rgba(52,211,153,0.25)',  icon:'üü¢', msg:'Results look mostly normal. Keep up healthy habits!' },
  INFO:   { color:'#38bdf8', bg:'rgba(56,189,248,0.08)',  border:'rgba(56,189,248,0.25)',  icon:'‚ÑπÔ∏è', msg:'This is a clinical/consultation report. No lab values detected.' },
};

export default function HomePage() {
  const [file, setFile]     = useState(null);
  const [drag, setDrag]     = useState(false);
  const [loading, setLoad]  = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError]   = useState('');
  const [info, setInfo]     = useState({ age:'', gender:'male', language:'en' });

  const onDrop = useCallback(e => {
    e.preventDefault(); setDrag(false);
    const f = e.dataTransfer.files[0];
    if (f) { setFile(f); setError(''); setResult(null); }
  }, []);

  const analyze = async () => {
    if (!file) { setError('Please select a file first.'); return; }
    setLoad(true); setError(''); setResult(null);
    try {
      const fd = new FormData(); fd.append('file', file);
      const up = await axios.post(`${API}/upload/report`, fd, { headers:{'Content-Type':'multipart/form-data'} });
      const pr = await axios.post(`${API}/process/report/${up.data.report_id}`, null, {
        params:{ file_path:up.data.file_path, patient_age:info.age||null, patient_gender:info.gender, language:info.language }
      });
      setResult(pr.data);
    } catch(e) {
      setError(e.response?.data?.detail || 'Failed to analyze. Make sure the backend is running on port 8000.');
    } finally { setLoad(false); }
  };

  const rc = result ? (RC[result.risk_level] || RC.INFO) : null;

  return (
    <div style={s.page}>
      {/* Hero */}
      <div style={s.hero} className="animate-up">
        <div className="section-label">Medical AI Analysis</div>
        <h1 style={s.h1}>Understand your report.<br/><span className="gradient-text">Plain and simple.</span></h1>
        <p style={s.sub}>Upload any lab report, prescription or clinical document ‚Äî our AI reads it and explains what it means for you.</p>
      </div>

      {/* Upload grid */}
      <div style={s.grid}>
        {/* Patient info card */}
        <div style={s.infoCard} className="glass-card animate-up delay-1">
          <h3 style={s.cardH}>Patient Details <span style={{fontSize:12,color:'#3d6090',fontWeight:400}}>(optional)</span></h3>
          <div style={s.fields}>
            <div>
              <label style={s.lbl}>Age</label>
              <input type="number" value={info.age} placeholder="e.g. 35" className="input-field"
                onChange={e=>setInfo({...info,age:e.target.value})}/>
            </div>
            <div>
              <label style={s.lbl}>Gender</label>
              <select value={info.gender} className="input-field" onChange={e=>setInfo({...info,gender:e.target.value})}>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div style={{gridColumn:'1/-1'}}>
              <label style={s.lbl}>Output Language</label>
              <select value={info.language} className="input-field" onChange={e=>setInfo({...info,language:e.target.value})}>
                <option value="en">üá∫üá∏ English</option>
                <option value="hi">üáÆüá≥ Hindi</option>
                <option value="bn">Bengali</option>
                <option value="ta">Tamil</option>
                <option value="te">Telugu</option>
                <option value="mr">Marathi</option>
              </select>
            </div>
          </div>
          <div style={s.fmts}>
            <span style={s.fmtLbl}>Formats:</span>
            {['PDF','JPG','PNG','BMP'].map(f=><span key={f} style={s.fmtTag}>{f}</span>)}
          </div>
        </div>

        {/* Drop zone col */}
        <div style={{display:'flex',flexDirection:'column',gap:16}}>
          <div style={{...s.drop, ...(drag?s.dropA:{}), ...(file?s.dropF:{})}} className="animate-up delay-2"
            onDragOver={e=>{e.preventDefault();setDrag(true);}} onDragLeave={()=>setDrag(false)} onDrop={onDrop}
            onClick={()=>document.getElementById('fi').click()}>
            <input id="fi" type="file" accept=".pdf,.jpg,.jpeg,.png,.bmp" style={{display:'none'}}
              onChange={e=>{setFile(e.target.files[0]);setError('');setResult(null);}}/>
            {file ? (
              <div style={{textAlign:'center',padding:32}}>
                <div style={{fontSize:48,marginBottom:12}}>{file.type.includes('pdf')?'üìÑ':'üñºÔ∏è'}</div>
                <div style={{fontSize:16,fontWeight:600,color:'#f0f6ff',marginBottom:4}}>{file.name}</div>
                <div style={{fontSize:13,color:'#7a9cc4',marginBottom:8}}>{(file.size/1024).toFixed(1)} KB</div>
                <div style={{fontSize:12,color:'#3d6090'}}>Click to change file</div>
              </div>
            ) : (
              <div style={{textAlign:'center',padding:32}}>
                <div style={{width:64,height:64,margin:'0 auto 16px',background:'rgba(56,189,248,0.08)',borderRadius:18,display:'flex',alignItems:'center',justifyContent:'center',animation:'float 4s ease-in-out infinite'}}>
                  <svg width="30" height="30" viewBox="0 0 24 24" fill="none" stroke="#38bdf8" strokeWidth="1.5">
                    <path d="M21 15v4a2 2 0 01-2 2H5a2 2 0 01-2-2v-4"/><polyline points="17,8 12,3 7,8"/><line x1="12" y1="3" x2="12" y2="15"/>
                  </svg>
                </div>
                <p style={{fontSize:16,fontWeight:500,color:'#a8c4e0',marginBottom:8}}>Drop your medical report here</p>
                <p style={{fontSize:13,color:'#3d6090'}}>or <span style={{color:'#38bdf8',fontWeight:600}}>browse files</span> ‚Äî PDF, JPG, PNG up to 10MB</p>
              </div>
            )}
          </div>

          {error && <div style={s.errBox} className="animate-up">‚ö†Ô∏è {error}</div>}

          <button onClick={analyze} disabled={loading||!file} style={{...s.abtn,...((!file||loading)?{opacity:0.5,cursor:'not-allowed'}:{})}}>
            {loading
              ? <span style={{display:'flex',alignItems:'center',justifyContent:'center',gap:10}}><span className="spinner"/>Analyzing your report...</span>
              : 'üîç  Analyze Report'}
          </button>
        </div>
      </div>

      {/* Results */}
      {result && (
        <div style={s.results} className="animate-up">
          {/* Risk banner */}
          <div style={{...s.rBanner, background:rc.bg, borderColor:rc.border}}>
            <div style={{display:'flex',alignItems:'center',gap:16}}>
              <span style={{fontSize:28}}>{rc.icon}</span>
              <div>
                <div style={{fontSize:16,fontWeight:800,letterSpacing:'1px',textTransform:'uppercase',color:rc.color}}>
                  {result.risk_level==='INFO'?'Clinical Report':`${result.risk_level} RISK`}
                </div>
                <div style={{fontSize:14,color:'#7a9cc4',marginTop:3}}>{rc.msg}</div>
              </div>
            </div>
            <div style={{padding:'6px 16px',borderRadius:'100px',border:`1px solid ${rc.border}`,fontSize:13,fontWeight:700,color:rc.color}}>
              {result.abnormal_values?.length>0 ? `${result.abnormal_values.length} abnormal value(s)` : 'No abnormal values'}
            </div>
          </div>

          {/* Abnormal values */}
          {result.abnormal_values?.length>0 && (
            <div style={{marginBottom:24}}>
              <h3 style={s.sTitle}>‚ö†Ô∏è Values Outside Normal Range</h3>
              <div style={s.valGrid}>
                {result.abnormal_values.map((v,i)=>(
                  <div key={i} style={s.valCard} className="glass-card">
                    <div style={{fontSize:13,fontWeight:600,color:'#7a9cc4',textTransform:'uppercase',letterSpacing:'0.5px',marginBottom:8}}>{v.test}</div>
                    <div style={{fontSize:28,fontWeight:800,fontFamily:'Fraunces,serif',letterSpacing:'-1px',color:v.status==='high'?'#fb7185':'#38bdf8',marginBottom:8}}>
                      {v.value}<span style={{fontSize:14,fontWeight:400,color:'#7a9cc4'}}> {v.unit}</span>
                    </div>
                    <div style={{display:'flex',gap:6,flexWrap:'wrap'}}>
                      <span style={{...s.tag2, ...(v.status==='high'?{background:'rgba(251,113,133,0.15)',color:'#fb7185'}:{background:'rgba(56,189,248,0.15)',color:'#38bdf8'})}}>{v.status.toUpperCase()}</span>
                      <span style={{...s.tag2,background:'rgba(245,158,11,0.1)',color:'#f59e0b'}}>{v.severity}</span>
                    </div>
                    <div style={{fontSize:11,color:'#3d6090',marginTop:8}}>Normal: {v.normal_range}</div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Explanation + Recs */}
          <div style={s.twoCol}>
            <div className="glass-card" style={{padding:24}}>
              <h3 style={s.sTitle}>üí° Simplified Explanation</h3>
              <pre style={{fontFamily:'DM Sans,sans-serif',fontSize:14,lineHeight:1.9,color:'#a8c4e0',whiteSpace:'pre-wrap'}}>{result.simplified_explanation}</pre>
            </div>
            <div style={{display:'flex',flexDirection:'column',gap:16}}>
              {result.recommendations?.length>0 && (
                <div className="glass-card" style={{padding:24}}>
                  <h3 style={s.sTitle}>‚úÖ What To Do</h3>
                  {result.recommendations.map((r,i)=>(
                    <div key={i} style={s.recRow}><span style={{color:'#38bdf8',fontWeight:700,minWidth:18}}>‚Üí</span><span style={{fontSize:14,color:'#a8c4e0',lineHeight:1.6}}>{r}</span></div>
                  ))}
                </div>
              )}
              {result.questions_to_ask_doctor?.length>0 && (
                <div className="glass-card" style={{padding:24}}>
                  <h3 style={s.sTitle}>‚ùì Ask Your Doctor</h3>
                  {result.questions_to_ask_doctor.map((q,i)=>(
                    <div key={i} style={s.recRow}><span style={{color:'#2dd4bf',fontWeight:700,minWidth:18}}>{i+1}.</span><span style={{fontSize:14,color:'#a8c4e0',lineHeight:1.6}}>{q}</span></div>
                  ))}
                </div>
              )}
            </div>
          </div>

          <div style={s.disc}>‚öïÔ∏è <strong>Disclaimer:</strong> MediExplain AI is for educational purposes only. Always consult a qualified healthcare professional.</div>
        </div>
      )}
    </div>
  );
}

const s = {
  page:{ padding:'88px 24px 60px',maxWidth:'1100px',margin:'0 auto' },
  hero:{ textAlign:'center',marginBottom:48 },
  h1:{ fontFamily:'Fraunces,serif',fontSize:'clamp(34px,5vw,54px)',fontWeight:600,lineHeight:1.15,color:'#f0f6ff',letterSpacing:'-1.5px',marginBottom:16,marginTop:10 },
  sub:{ fontSize:17,color:'#7a9cc4',maxWidth:520,margin:'0 auto',lineHeight:1.7 },
  grid:{ display:'grid',gridTemplateColumns:'320px 1fr',gap:24,marginBottom:32 },
  infoCard:{ padding:28 },
  cardH:{ fontFamily:'Fraunces,serif',fontSize:18,fontWeight:600,color:'#f0f6ff',marginBottom:20 },
  fields:{ display:'grid',gridTemplateColumns:'1fr 1fr',gap:14,marginBottom:24 },
  lbl:{ display:'block',fontSize:12,fontWeight:600,color:'#7a9cc4',marginBottom:8,textTransform:'uppercase',letterSpacing:'0.5px' },
  fmts:{ display:'flex',alignItems:'center',gap:8,flexWrap:'wrap' },
  fmtLbl:{ fontSize:11,color:'#3d6090',fontWeight:600,textTransform:'uppercase',letterSpacing:'0.5px' },
  fmtTag:{ background:'rgba(56,189,248,0.08)',border:'1px solid rgba(56,189,248,0.15)',borderRadius:5,padding:'3px 8px',fontSize:11,color:'#38bdf8',fontWeight:600 },
  drop:{ flex:1,minHeight:240,border:'2px dashed rgba(56,189,248,0.2)',borderRadius:18,display:'flex',alignItems:'center',justifyContent:'center',cursor:'pointer',transition:'all 0.25s',background:'rgba(56,189,248,0.02)' },
  dropA:{ borderColor:'#38bdf8',background:'rgba(56,189,248,0.07)',transform:'scale(1.01)' },
  dropF:{ borderColor:'rgba(45,212,191,0.4)',background:'rgba(45,212,191,0.04)' },
  errBox:{ background:'rgba(251,113,133,0.08)',border:'1px solid rgba(251,113,133,0.25)',borderRadius:10,padding:'12px 16px',color:'#fb7185',fontSize:14 },
  abtn:{ width:'100%',padding:'16px',background:'linear-gradient(135deg,#0ea5e9,#2dd4bf)',color:'#fff',border:'none',borderRadius:14,fontFamily:'DM Sans,sans-serif',fontSize:16,fontWeight:700,cursor:'pointer',transition:'all 0.2s' },
  results:{ marginTop:40 },
