            <div className="animate-up">
              <div style={{display:'flex',gap:20,alignItems:'flex-start',marginBottom:20}}>
                <div style={{width:60,height:60,background:'rgba(56,189,248,0.08)',border:'1px solid rgba(56,189,248,0.2)',borderRadius:16,display:'flex',alignItems:'center',justifyContent:'center',fontSize:28,flexShrink:0}}>ðŸ§¬</div>
                <div>
                  <h2 style={{fontFamily:'Fraunces,serif',fontSize:28,fontWeight:600,color:'#f0f6ff',letterSpacing:'-0.5px',marginBottom:6}}>{det.name}</h2>
                  <p style={{fontSize:14,color:'#7a9cc4',lineHeight:1.7}}>{det.description}</p>
                </div>
              </div>
              <div style={{display:'grid',gridTemplateColumns:'1fr 1fr',gap:12,marginBottom:16}}>
                {[{g:'â™‚ Male',v:det.normal_range_male},{g:'â™€ Female',v:det.normal_range_female}].map((r,i)=>(
                  <div key={i} style={{background:'rgba(56,189,248,0.05)',border:'1px solid rgba(56,189,248,0.12)',borderRadius:12,padding:16}}>
                    <div style={{fontSize:11,fontWeight:700,color:'#7a9cc4',textTransform:'uppercase',letterSpacing:'0.5px',marginBottom:6}}>{r.g} Normal Range</div>
                    <div style={{fontSize:18,fontWeight:700,color:'#38bdf8',fontFamily:'Fraunces,serif'}}>{r.v}</div>
                  </div>
                ))}
              </div>
              {[
                {c:'rgba(251,113,133,0.25)',bg:'rgba(251,113,133,0.05)',col:'#fb7185',lbl:'ðŸ”º If Result is HIGH',txt:det.high_meaning},
                {c:'rgba(56,189,248,0.25)',bg:'rgba(56,189,248,0.05)',col:'#38bdf8',lbl:'ðŸ”» If Result is LOW',txt:det.low_meaning},
              ].map((x,i)=>(
                <div key={i} style={{border:`1px solid ${x.c}`,background:x.bg,borderRadius:12,padding:16,marginBottom:12}}>
                  <div style={{fontSize:12,fontWeight:700,textTransform:'uppercase',letterSpacing:'0.5px',color:x.col,marginBottom:8}}>{x.lbl}</div>
                  <p style={{fontSize:14,color:'#a8c4e0',lineHeight:1.7}}>{x.txt}</p>
                </div>
              ))}
            </div>
          )}

          {!load && det && tab==='meds' && (
            <div className="animate-up">
              <div style={{display:'flex',gap:20,alignItems:'flex-start',marginBottom:20}}>
                <div style={{width:60,height:60,background:'rgba(56,189,248,0.08)',border:'1px solid rgba(56,189,248,0.2)',borderRadius:16,display:'flex',alignItems:'center',justifyContent:'center',fontSize:28,flexShrink:0}}>ðŸ’Š</div>
                <div>
                  <h2 style={{fontFamily:'Fraunces,serif',fontSize:28,fontWeight:600,color:'#f0f6ff',letterSpacing:'-0.5px',marginBottom:8}}>{det.name}</h2>
                  <span style={{display:'inline-block',background:'rgba(56,189,248,0.1)',border:'1px solid rgba(56,189,248,0.25)',borderRadius:'100px',padding:'4px 12px',fontSize:12,color:'#38bdf8',fontWeight:600}}>{det.category}</span>
                </div>
              </div>
              {[
                {c:'rgba(52,211,153,0.25)',bg:'rgba(52,211,153,0.05)',col:'#34d399',lbl:'ðŸŽ¯ Purpose',txt:det.purpose},
                {c:'rgba(245,158,11,0.25)',bg:'rgba(245,158,11,0.05)',col:'#f59e0b',lbl:'âš ï¸ Side Effects',sideEffects:det.side_effects},
                {c:'rgba(167,139,250,0.25)',bg:'rgba(167,139,250,0.05)',col:'#a78bfa',lbl:'ðŸ“‹ Precautions',txt:det.precautions},
              ].map((x,i)=>(
                <div key={i} style={{border:`1px solid ${x.c}`,background:x.bg,borderRadius:12,padding:16,marginBottom:12}}>
                  <div style={{fontSize:12,fontWeight:700,textTransform:'uppercase',letterSpacing:'0.5px',color:x.col,marginBottom:8}}>{x.lbl}</div>
                  {x.sideEffects
                    ? <div style={{display:'flex',flexWrap:'wrap',gap:8}}>{x.sideEffects.map((se,j)=><span key={j} style={{background:'rgba(245,158,11,0.1)',border:'1px solid rgba(245,158,11,0.2)',borderRadius:6,padding:'4px 10px',fontSize:12,color:'#f59e0b'}}>{se}</span>)}</div>
                    : <p style={{fontSize:14,color:'#a8c4e0',lineHeight:1.7}}>{x.txt}</p>
                  }
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

