import React, { useState, useEffect } from 'react';

const rC = { HIGH:'#fb7185', MEDIUM:'#f59e0b', LOW:'#34d399', INFO:'#38bdf8' };
const rB = { HIGH:'rgba(251,113,133,0.12)', MEDIUM:'rgba(245,158,11,0.12)', LOW:'rgba(52,211,153,0.12)', INFO:'rgba(56,189,248,0.12)' };

const STATIC_TIPS = [
  { i:'ü•ó', c:'Diet',      t:'Increase leafy vegetables ‚Äî spinach, kale, and fenugreek help with low hemoglobin.', col:'#34d399' },
  { i:'üèÉ', c:'Exercise',  t:'Walk 30 minutes daily ‚Äî improves blood sugar and cholesterol simultaneously.',       col:'#38bdf8' },
  { i:'üíß', c:'Hydration', t:'Drink 8-10 glasses of water daily to support kidney function and circulation.',      col:'#2dd4bf' },
  { i:'üò¥', c:'Sleep',     t:'Poor sleep raises blood sugar. Aim for 7‚Äì8 hours of quality sleep every night.',     col:'#a78bfa' },
];

// ‚îÄ‚îÄ helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
function loadReports() {
  try { return JSON.parse(localStorage.getItem('medi_reports') || '[]'); } catch { return []; }
}

function getRiskOrder(r) { return { HIGH:3, MEDIUM:2, LOW:1, INFO:0 }[r] ?? 0; }

function buildStats(reports) {
  if (!reports.length) return null;
  const latest      = reports[reports.length - 1];
  const currentRisk = latest.risk_level || 'LOW';
  const improved    = reports.filter((r, i) =>
    i > 0 && getRiskOrder(r.risk_level) < getRiskOrder(reports[i - 1].risk_level)
  ).length;
  const firstDate = new Date(reports[0].date);
  const days      = Math.max(1, Math.round((Date.now() - firstDate) / 86400000));
  return { count: reports.length, currentRisk, improved, days };
}

function buildTrends(reports) {
  // Collect all unique test names across all reports
  const testMap = {};
  reports.forEach(rep => {
    (rep.all_values || rep.abnormal_values || []).forEach(v => {
      if (!testMap[v.test]) testMap[v.test] = { name: v.test, unit: v.unit || '', entries: [] };
      testMap[v.test].entries.push({ date: rep.date, value: parseFloat(v.value) });
    });
  });
  // Only show tests that appear in ‚â•2 reports and have numeric values
  return Object.values(testMap)
    .filter(t => t.entries.length >= 2 && t.entries.every(e => !isNaN(e.value)))
    .slice(0, 4)
    .map((t, idx) => ({
      name:    t.name,
      unit:    t.unit,
      vals:    t.entries.map(e => e.value),
      dates:   t.entries.map(e => fmtDate(e.date)),
      normal:  null,  // we don't always have normal from backend; skip line
      color:   ['#f59e0b','#fb7185','#38bdf8','#a78bfa'][idx % 4],
    }));
}

function fmtDate(iso) {
  try {
    const d = new Date(iso);
    return d.toLocaleDateString('en-US', { month:'short', day:'numeric' });
  } catch { return iso; }
}

function progressMsg(reports) {
  if (reports.length < 2) return null;
  const first = reports[0].risk_level;
  const last  = reports[reports.length - 1].risk_level;
  if (getRiskOrder(last) < getRiskOrder(first))
    return `‚úÖ Great progress! You've gone from ${first} risk to ${last} risk.`;
  if (getRiskOrder(last) > getRiskOrder(first))
    return `‚ö†Ô∏è Your risk has increased from ${first} to ${last}. Please consult a doctor.`;
  return `‚ÑπÔ∏è Your risk level has stayed at ${last} across ${reports.length} reports.`;
}

// ‚îÄ‚îÄ component ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
export default function DashboardPage() {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    setReports(loadReports());
    // Re-sync if user goes back to home and uploads
    const onStorage = () => setReports(loadReports());
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  const hasData = reports.length > 0;
  const stats   = buildStats(reports);
  const trends  = buildTrends(reports);
  const prog    = progressMsg(reports);

  // ‚îÄ‚îÄ EMPTY STATE ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  if (!hasData) {
    return (
      <div style={s.page}>
        <div style={s.hero} className="animate-up">
          <div className="section-label">Health Dashboard</div>
          <h1 style={s.h1}>Your health over time</h1>
          <p style={s.sub}>Track your progress as you upload reports</p>
        </div>
        <div className="glass-card animate-up delay-1" style={{padding:60,textAlign:'center',marginBottom:20}}>
          <div style={{fontSize:64,marginBottom:20,opacity:0.4}}>üìä</div>
          <h3 style={{fontFamily:'Fraunces,serif',fontSize:24,fontWeight:600,color:'#f0f6ff',marginBottom:12}}>No reports yet</h3>
          <p style={{color:'#7a9cc4',fontSize:15,lineHeight:1.7,maxWidth:400,margin:'0 auto'}}>
            Upload your first medical report on the <strong style={{color:'#38bdf8'}}>Analyze</strong> page. 
            Your history, risk trends, and personalized insights will appear here automatically.
          </p>
        </div>
        <TipsSection />
      </div>
    );
  }

  // ‚îÄ‚îÄ FULL DASHBOARD ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  return (
    <div style={s.page}>
      <div style={s.hero} className="animate-up">
        <div className="section-label">Health Dashboard</div>
        <h1 style={s.h1}>Your health over time</h1>
        <p style={s.sub}>Track your progress and see how your values have changed</p>
      </div>

      {/* ‚îÄ‚îÄ Stats row ‚îÄ‚îÄ */}
      <div style={s.statsRow} className="animate-up delay-1">
        {[
          { l:'Reports Analyzed', v: String(stats.count),       i:'üìÑ', c:'#38bdf8' },
          { l:'Current Risk',     v: stats.currentRisk,         i: stats.currentRisk==='LOW'?'üü¢':stats.currentRisk==='MEDIUM'?'üü°':'üî¥', c: rC[stats.currentRisk] },
          { l:'Risk Improvements',v: String(stats.improved),    i:'üìà', c:'#2dd4bf' },
          { l:'Days Tracked',     v: String(stats.days),        i:'üìÖ', c:'#a78bfa' },
        ].map((x,i)=>(
          <div key={i} className="glass-card" style={{padding:'24px 20px',textAlign:'center'}}>
            <div style={{fontSize:26,marginBottom:10}}>{x.i}</div>
            <div style={{fontFamily:'Fraunces,serif',fontSize:30,fontWeight:700,color:x.c,letterSpacing:'-0.5px',marginBottom:6}}>{x.v}</div>
            <div style={{fontSize:12,color:'#7a9cc4',fontWeight:500,textTransform:'uppercase',letterSpacing:'0.5px'}}>{x.l}</div>
          </div>
        ))}
      </div>

      {/* ‚îÄ‚îÄ Timeline ‚îÄ‚îÄ */}
      <div className="glass-card animate-up delay-2" style={{padding:28,marginBottom:20}}>
        <h3 style={s.sT}>üìã Report History</h3>
        <div style={{display:'flex',gap:0,alignItems:'flex-start',overflowX:'auto',paddingBottom:8}}>
          {reports.map((r,i)=>{
            const risk = r.risk_level || 'INFO';
            const ab   = r.abnormal_values?.length ?? 0;
            const tot  = (r.all_values?.length || ab) || '‚Äî';
            return (
              <div key={i} style={{display:'flex',flexDirection:'column',alignItems:'center',flex:1,minWidth:130,position:'relative'}}>
                <div style={{width:12,height:12,borderRadius:'50%',background:rC[risk]||'#38bdf8',zIndex:1,marginBottom:10,flexShrink:0}}/>
                {i<reports.length-1 && <div style={{position:'absolute',top:6,left:'50%',width:'100%',height:2,background:'rgba(56,189,248,0.1)',zIndex:0}}/>}
                <div style={{borderRadius:12,padding:'12px 14px',textAlign:'center',width:'90%',background:rB[risk]||rB.INFO,border:`1px solid ${(rC[risk]||'#38bdf8')}30`}}>
                  <div style={{fontSize:11,color:'#7a9cc4',fontWeight:600,marginBottom:4,textTransform:'uppercase',letterSpacing:'0.5px'}}>{fmtDate(r.date)}</div>
                  <div style={{fontSize:13,fontWeight:800,letterSpacing:'0.5px',color:rC[risk]||'#38bdf8',marginBottom:4}}>{risk}</div>
                  <div style={{fontSize:11,color:'#7a9cc4'}}>{ab} abnormal</div>
                </div>
              </div>
            );
          })}
        </div>
        {prog && (
          <div style={{marginTop:16,padding:'12px 16px',background:prog.startsWith('‚úÖ')?'rgba(52,211,153,0.08)':prog.startsWith('‚ö†Ô∏è')?'rgba(245,158,11,0.08)':'rgba(56,189,248,0.08)',border:`1px solid ${prog.startsWith('‚úÖ')?'rgba(52,211,153,0.2)':prog.startsWith('‚ö†Ô∏è')?'rgba(245,158,11,0.2)':'rgba(56,189,248,0.2)'}`,borderRadius:10,fontSize:14,color:prog.startsWith('‚úÖ')?'#34d399':prog.startsWith('‚ö†Ô∏è')?'#f59e0b':'#38bdf8'}}>
            {prog}
          </div>
        )}
      </div>

      {/* ‚îÄ‚îÄ Trends (only if ‚â•2 reports have matching test names) ‚îÄ‚îÄ */}
      {trends.length > 0 ? (
        <div className="glass-card animate-up delay-3" style={{padding:28,marginBottom:20}}>
          <h3 style={s.sT}>üìâ Value Trends</h3>
          <div style={{display:'flex',flexDirection:'column',gap:32}}>
            {trends.map((t,ti)=>{
              const max = Math.max(...t.vals)*1.15 || 1;
              return (
                <div key={ti}>
                  <div style={{display:'flex',alignItems:'center',gap:10,marginBottom:12}}>
                    <span style={{fontSize:14,fontWeight:700,color:t.color}}>{t.name}</span>
                    <span style={{fontSize:12,color:'#3d6090'}}>{t.unit}</span>
                  </div>
                  <div style={{display:'flex',gap:12,alignItems:'flex-end',height:100}}>
                    {t.vals.map((v,i)=>{
                      const h = Math.max(8,(v/max)*80);
                      return (
                        <div key={i} style={{display:'flex',flexDirection:'column',alignItems:'center',gap:4,flex:1}}>
                          <div style={{fontSize:11,fontWeight:700,color:'#7a9cc4'}}>{v}</div>
                          <div style={{height:`${h}px`,background:`linear-gradient(to top,${t.color},${t.color}88)`,borderRadius:'6px 6px 0 0',minWidth:36,transition:'height 0.8s ease'}}/>
                          <div style={{fontSize:10,color:'#3d6090',fontWeight:600,textAlign:'center'}}>{t.dates[i]}</div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      ) : reports.length >= 2 && (
        <div className="glass-card animate-up delay-3" style={{padding:28,marginBottom:20,textAlign:'center'}}>
          <div style={{fontSize:40,marginBottom:12,opacity:0.4}}>üìâ</div>
          <p style={{color:'#7a9cc4',fontSize:14}}>Value trends will appear once the same tests appear in multiple reports.</p>
        </div>
      )}

      {/* ‚îÄ‚îÄ Latest report detail ‚îÄ‚îÄ */}
      {reports.length > 0 && reports[reports.length-1].abnormal_values?.length > 0 && (
        <div className="glass-card animate-up delay-3" style={{padding:28,marginBottom:20}}>
          <h3 style={s.sT}>‚ö†Ô∏è Latest Abnormal Values</h3>
          <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(170px,1fr))',gap:14}}>
            {reports[reports.length-1].abnormal_values.map((v,i)=>{
              const col = v.status==='high'?'#fb7185':'#38bdf8';
              return (
                <div key={i} style={{background:'rgba(255,255,255,0.02)',border:`1px solid ${col}30`,borderRadius:12,padding:16}}>
                  <div style={{fontSize:12,fontWeight:600,color:'#7a9cc4',textTransform:'uppercase',letterSpacing:'0.5px',marginBottom:6}}>{v.test}</div>
                  <div style={{fontSize:24,fontWeight:800,fontFamily:'Fraunces,serif',color:col,marginBottom:6}}>
                    {v.value}<span style={{fontSize:12,fontWeight:400,color:'#7a9cc4'}}> {v.unit}</span>
                  </div>
                  <div style={{fontSize:11,background:`${col}18`,color:col,padding:'3px 8px',borderRadius:6,display:'inline-block',fontWeight:700,marginBottom:4}}>
                    {v.status?.toUpperCase()} ¬∑ {v.severity}
                  </div>
                  <div style={{fontSize:11,color:'#3d6090',marginTop:4}}>Normal: {v.normal_range}</div>
                </div>
              );
            })}
          </div>
        </div>
      )}

